import React from 'react';
import socket from './socket';
import axios from 'axios';
import '../chatPage.css';
import { Button, Form, Container, Row, Col } from 'react-bootstrap';

const ChatPage = ({users, messages, userName, roomId, onAddMessage}) => {

    const [messageValue, setMessageValue] = React.useState('');

    const onSendMessage = () => {
        socket.emit('ROOM:NEW_MESSAGE', {
            userName,
            roomId,
            text: messageValue
        });
        onAddMessage({userName, text: messageValue});
        setMessageValue('');
    }

    return (
        <Container className="chat">
            <Row>
                <Col className="chat__users">
                    <b> Room {roomId} </b>
                    <hr />
                    <p><b> Users list({users.length}): </b></p>
                    <ul>
                        {users.map((name, index) => (<li key={name + index}>{name}</li>))}
                    </ul>
                </Col>
                <Col className="chat__messages">
                    <div className="messages">
                        <p>hello</p>
                        <p>hello</p>
                        {messages.map((mess, index) => (
                            <div key={mess + index} className="message">
                                <div>
                                    <p key={mess + index} >{mess.text}</p>
                                </div>
                                <div>
                                    <span key={mess + index} >{mess.userName}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="send-message-form">
                        <Form.Control className="form-control" as="textarea" rows="3" 
                        value={messageValue}
                        onChange={(e) => setMessageValue(e.target.value)} ></Form.Control> 
                        <Button variant="secondary" onClick={onSendMessage}>Send</Button>
                    </div>
                </Col>
            </Row>
        </Container>
    )
}

export default ChatPage;
